// 准备查询参数
const maxCheckedId = $('查询已检查的最大BugID').first().json.max_checked_bug_id || 0;

return [{
  json: {
    max_checked_bug_id: maxCheckedId,
    check_time: new Date().toISOString()
  }
}];

// 准备调用Dify的数据 - 将每条障害格式化为AI诊断请求
const bugs = $input.all();

if (bugs.length === 0) {
  return [];
}

// 为每条障害生成Dify请求数据
const difyRequests = bugs.map(item => {
  const bug = item.json;

  // 构建诊断查询文本
  const query = `请分析以下障害信息并提供诊断建议：

障害编号：${bug.bug_id}
项目名称：${bug.project_name || '未指定'}
分类：${bug.category_name || '未分类'}
概要：${bug.summary || '无'}

详细描述：
${bug.detail_description || '无详细描述'}

请基于历史案例提供：
1. 相似案例分析
2. 根本原因分析
3. 诊断建议和定位方向
4. 修正方案参考`;

  return {
    json: {
      bug_id: bug.bug_id,
      project_name: bug.project_name,
      category_name: bug.category_name,
      summary: bug.summary,
      detail_description: bug.detail_description,
      dify_query: query,
      request_time: new Date().toISOString()
    }
  };
});

return difyRequests;

// 处理Dify streaming返回结果
const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const originalData = $('准备Dify请求').all()[i].json;

  let aiSuggestions = '';
  let status = 'failed';
  let errorMessage = null;

  try {
    const response = item.json;

    // 处理streaming格式：data字段包含SSE流
    if (response && response.data) {
      const streamData = response.data;

      // 解析SSE格式的数据流
      const lines = streamData.split('\n');
      let fullAnswer = '';

      for (const line of lines) {
        if (line.startsWith('data: ')) {
          try {
            const jsonStr = line.substring(6); // 移除 "data: " 前缀
            const event = JSON.parse(jsonStr);

            // 只收集agent_message事件的answer字段（累加增量）
            if (event.event === 'agent_message' && event.answer) {
              fullAnswer += event.answer;
            }
          } catch (parseError) {
            // 跳过无法解析的行
            continue;
          }
        }
      }

      if (fullAnswer) {
        aiSuggestions = fullAnswer.trim();
        status = 'success';
      } else {
        errorMessage = 'Dify streaming响应中未找到answer字段';
      }
    } else {
      errorMessage = 'Dify返回格式异常: ' + JSON.stringify(response).substring(0, 200);
    }
  } catch (error) {
    errorMessage = 'AI响应解析失败: ' + error.message;
  }

  results.push({
    json: {
      bug_id: originalData.bug_id,
      ai_suggestions: aiSuggestions,
      status: status,
      error_message: errorMessage,
      request_time: originalData.request_time,
      response_time: new Date().toISOString(),
      project_name: originalData.project_name,
      summary: originalData.summary
    }
  });
}

return results;

parameters.query:
INSERT INTO mantis.bug_ai_diagnosis (
  bug_id,
  ai_suggestions,
  status,
  error_message,
  request_time,
  response_time,
  project_name,
  bug_summary
)
VALUES
{{ $input.all().map(item => {
  const d = item.json;
  function pgEscape(str) {
    if (!str || str === 'null' || str === 'NULL') return 'NULL';
    let tag = 'ai';
    const s = String(str);
    while (s.includes(`$${tag}$`)) { tag = tag + '_'; }
    return `$${tag}$` + s + `$${tag}$`;
  }
  return `(
    ${d.bug_id},
    ${pgEscape(d.ai_suggestions)},
    ${pgEscape(d.status)},
    ${pgEscape(d.error_message)},
    ${pgEscape(d.request_time)},
    ${pgEscape(d.response_time)},
    ${pgEscape(d.project_name)},
    ${pgEscape(d.summary)}
  )`;
}).join(',\n') }}
ON CONFLICT (bug_id) DO UPDATE SET
  ai_suggestions = EXCLUDED.ai_suggestions,
  status = EXCLUDED.status,
  error_message = EXCLUDED.error_message,
  response_time = EXCLUDED.response_time,
  updated_at = NOW();

