// 生成通知汇总
const aiResults = $('处理AI响应').all();
const bugs = $('查询新规障害').all();
const checkTime = $('准备查询参数').first().json.check_time;

if (bugs.length === 0) {
  return [{ json: { message: '没有新的障害', count: 0 } }];
}

const successCount = aiResults.filter(r => r.json.status === 'success').length;
const failedCount = aiResults.filter(r => r.json.status === 'failed').length;

const lines = [
  `📢 *新规障害AI诊断通知* - ${new Date(checkTime).toLocaleString('ja-JP')}`,
  ``,
  `发现 *${bugs.length}* 个新障害，AI诊断完成：`,
  `✅ 成功: ${successCount}个`,
  `❌ 失败: ${failedCount}个`,
  ``,
  `---`,
  ``
];

bugs.forEach((item, index) => {
  const bug = item.json;
  const aiResult = aiResults[index]?.json;
  const registeredDate = bug.registered_at ? new Date(bug.registered_at).toLocaleString('ja-JP') : 'N/A';

  lines.push(`*${index + 1}. Bug #${bug.bug_id}* ${aiResult?.status === 'success' ? '✅' : '❌'}`);
  lines.push(`  📋 概要: ${bug.summary || 'N/A'}`);
  lines.push(`  📁 项目: ${bug.project_name || 'N/A'}`);
  lines.push(`  📂 分类: ${bug.category_name || 'N/A'}`);
  lines.push(`  🕐 登录日期: ${registeredDate}`);

  if (aiResult?.status === 'success') {
    const suggestion = aiResult.ai_suggestions.substring(0, 150) + '...';
    lines.push(`  🤖 AI诊断: ${suggestion}`);
  } else if (aiResult?.error_message) {
    lines.push(`  ⚠️ 诊断失败: ${aiResult.error_message}`);
  }

  lines.push(``);
});

lines.push(`---`);
lines.push(`最大BugID: ${Math.max(...bugs.map(b => b.json.bug_id))}`);

const formattedMessage = lines.join('\n');

return [{
  json: {
    message: formattedMessage,
    count: bugs.length,
    success_count: successCount,
    failed_count: failedCount,
    max_bug_id: Math.max(...bugs.map(b => b.json.bug_id)),
    check_time: checkTime
  }
}];

parameters.query:
INSERT INTO mantis.new_bug_monitor_log (bug_id, check_time, bug_summary, project_name, category_name, registered_at)
VALUES 
{{ $('查询新规障害').all().map(item => 
  `(${item.json.bug_id}, NOW(), $n8n$${item.json.summary || ''}$n8n$, $n8n$${item.json.project_name || ''}$n8n$, $n8n$${item.json.category_name || ''}$n8n$, $n8n$${item.json.registered_at || ''}$n8n$)`
).join(',\n') }}
ON CONFLICT (bug_id) DO UPDATE SET
  check_time = EXCLUDED.check_time;

CREATE TABLE IF NOT EXISTS mantis.new_bug_monitor_log (
  id BIGSERIAL PRIMARY KEY,
  bug_id INTEGER NOT NULL UNIQUE,
  check_time TIMESTAMP NOT NULL DEFAULT NOW(),
  bug_summary TEXT,
  project_name VARCHAR(255),
  category_name VARCHAR(255),
  registered_at TIMESTAMP,
  created_at TIMESTAMP DEFAULT NOW()
);

CREATE INDEX IF NOT EXISTS idx_new_bug_monitor_bug_id ON mantis.new_bug_monitor_log(bug_id);
CREATE INDEX IF NOT EXISTS idx_new_bug_monitor_check_time ON mantis.new_bug_monitor_log(check_time);
CREATE INDEX IF NOT EXISTS idx_new_bug_monitor_registered_at ON mantis.new_bug_monitor_log(registered_at);

CREATE TABLE IF NOT EXISTS mantis.bug_ai_diagnosis (
  id BIGSERIAL PRIMARY KEY,
  bug_id INTEGER NOT NULL UNIQUE,
  ai_suggestions TEXT,
  status VARCHAR(20) NOT NULL DEFAULT 'pending',
  error_message TEXT,
  request_time TIMESTAMP NOT NULL,
  response_time TIMESTAMP,
  project_name VARCHAR(255),
  bug_summary TEXT,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

CREATE INDEX IF NOT EXISTS idx_bug_ai_diagnosis_bug_id ON mantis.bug_ai_diagnosis(bug_id);
CREATE INDEX IF NOT EXISTS idx_bug_ai_diagnosis_status ON mantis.bug_ai_diagnosis(status);
CREATE INDEX IF NOT EXISTS idx_bug_ai_diagnosis_created_at ON mantis.bug_ai_diagnosis(created_at);

COMMENT ON TABLE mantis.bug_ai_diagnosis IS 'AI障害诊断结果表';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.bug_id IS '障害编号';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.ai_suggestions IS 'AI诊断建议内容';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.status IS '诊断状态: success/failed/pending';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.error_message IS '失败时的错误信息';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.request_time IS '发起请求时间';
COMMENT ON COLUMN mantis.bug_ai_diagnosis.response_time IS '收到响应时间';
