INSERT INTO mantis.bug_ai_diagnosis (
  bug_id,
  ai_suggestions,
  status,
  error_message,
  request_time,
  response_time,
  project_name,
  bug_summary
)
VALUES
{{ $input.all().map(item => {
  const d = item.json;
  function pgEscape(str) {
    if (!str || str === 'null' || str === 'NULL') return 'NULL';
    let tag = 'ai';
    const s = String(str);
    while (s.includes(`$${tag}$`)) { tag = tag + '_'; }
    return `$${tag}$` + s + `$${tag}$`;
  }
  return `(
    ${d.bug_id},
    ${pgEscape(d.ai_suggestions)},
    ${pgEscape(d.status)},
    ${pgEscape(d.error_message)},
    ${pgEscape(d.request_time)},
    ${pgEscape(d.response_time)},
    ${pgEscape(d.project_name)},
    ${pgEscape(d.summary)}
  )`;
}).join(',\n') }}
ON CONFLICT (bug_id) DO UPDATE SET
  ai_suggestions = EXCLUDED.ai_suggestions,
  status = EXCLUDED.status,
  error_message = EXCLUDED.error_message,
  response_time = EXCLUDED.response_time,
  updated_at = NOW();

