function cleanText(text) {
  if (!text || text.trim() === '') return '';
  text = text.replace(/\\r\\n/g, '\n').replace(/\\n/g, '\n').replace(/\r\n/g, '\n');
  text = text.replace(/\n\s*\n/g, '\n\n');
